<form id="data_employee" method="post" enctype="multipart/form-data">    <!-- page content -->    <div class="row">               <div class="col-3">				<div id="OpenImgUpload" title="Select avatar" class="card" style="width: 18rem; height:18rem; margin: 0 auto; color:#fff;">			<img id="blah" style="width: 18rem; height:18rem;  object-fit: contain;" src="{{ isset($users->avatar) ? $users->avatar :  url('/storage/avatars/system/default_avatar.jpg') }}" class="card-img-top" alt="...">   		</div>														<br/>				<br/>				<br/>		<div class="row">			<div class="form-group col-sm-8 form-input-employee">				<label for="employee_name">Employee name</label>				<input type="text" class="form-control" id="employee_name" name="employee_name" value="{{ $users->employee_name ?? '' }}" placeholder="">			</div>		</div>						<br/>				<div class="row">			<div class="form-group col-sm-8 form-input-employee">				<label for="employee_id">Employee ID</label>				<input type="text" class="form-control" id="employee_id" name="employee_id" value="{{ $users->employee_id ?? '' }}" placeholder="">			</div>		</div>		  							<br/>				<div class="row">			<div class="form-group col-sm-8 form-input-employee">				<label for="first_name">Full name</label>				<input type="text" class="form-control" id="full_name" name="full_name" value="{{ $users->full_name ?? '' }}" placeholder="">			</div>		</div>								<br/>				<div class="row">			<div class="form-group col-sm-8 form-input-employee">				<label for="name_pronouciation">Name pronouciation</label>				<input type="text" class="form-control" id="name_pronouciation" name="name_pronouciation" value="{{ $users->name_pronouciation ?? '' }}" placeholder="">			</div>		</div>									<br/>				<div class="row">			<div class="form-group col-sm-8 form-input-employee">				<label for="position">Position name</label>				<input type="text" class="form-control" id="position" name="position" value="{{ 