<div class="row project-details" id="project-details-container">    <div class="col-sm-12 tinymce-transparent">        <!--textarea & editor area-->        <div class="project-description p-0 p-t-40 rich-text-formatting" id="project-description"> {!!            clean($client->client_description) !!}        </div>        <!--dynamic description field-->        <input type="hidden" name="description" id="description" value="">             <!--buttons: edit-->        <div id="project-description-edit" class="p-t-20 text-right">            <button type="button" class="btn waves-effect waves-light btn-xs btn-info"                id="project-description-button-edit">{{ cleanLang(__('lang.edit_description')) }}</button>        </div>        <!--button: subit & cancel-->        <div id="project-description-submit" class="p-t-20 hidden text-right">            <button type="button" class="btn waves-effect waves-light btn-xs btn-default"                id="project-description-button-cancel">{{ cleanLang(__('lang.cancel')) }}</button>            <button type="button" class="btn waves-effect waves-light btn-xs btn-danger" data-type="form"                data-form-id="project-details-container" data-ajax-type="post"                data-url="{{ url('/clients') }}/{{ $client->client_id }}/client-details"                id="project-description-button-save">{{ cleanLang(__('lang.save')) }}</button>        </div>           </div></div>