lass="col-md-12">													<label for="company_vision">Vision</label>				<textarea class="form-control" id="company_vision" name="company_vision" rows="10"><?php echo e(isset($company->company_vision) ? $company->company_vision : ''); ?></textarea>														</div>											</div>				<br/>		<div class="row">			<div class="col-md-12">													<label for="company_values_culture">Values & Culture</label>				<textarea class="form-control" id="company_values_culture" name="company_values_culture" rows="10"><?php echo e(isset($company->company_values_culture) ? $company->company_values_culture : ''); ?></textarea>																	</div>											</div>		<br/>		<div class="row">			<div class="col-md-12 form-input-employee">				<button type="submit" class="btn btn-rounded-x btn-danger waves-effect btn_employee" data-on-start-submit-button="disable">Submit</button>			</div>							</div>		<input type="file" id="imgupload" name="imgupload" accept="image/*"  style="display:none"/> 			<br/>		<br/>                    <!-- Column -->				</form></div>    <!--page content --><script>var id = "<?php echo e(isset($company->company_id) ? '/' .$company->company_id : ''); ?>";var url = "<?php echo e($page['action_url']); ?>"+id;console.log(url);$("form#data_company").submit(function(e) {		var obj = {};		var csrf_token = "<?php echo e(csrf_token()); ?>";	e.preventDefault();	var company_name = $('#company_name').val();		if(company_name == ''){		$('#company_name').addClass("errors");	}		if(company_name == '') return false;	var formData = new FormData(this);	$.ajaxSetup({		headers: {			'X-CSRF-TOKEN': "<?php echo e(csrf_token()); ?>"		}	});	$.ajax({		url: url,		type: 'POST',		data: formData,		success: function (response) {			//var returnedData = JSON.parse(response);			var notification = response['notification'];			obj['type'] = notification['type'];			obj['message'] = notification['value'];			NX.notification(obj);						},		cache: false,		contentType: false,		processData: false	});});$('#OpenImgUpload').click(function(){ $('#imgupload').trigger('click'); });$("#imgupload").change(function(){		$('#blah').attr('src', '');	readURL(this);});function readURL(input) {   if (input.files && input.files[0]) {        var reader = new FileReader();                    reader.onload = function (e) {            $('#blah').attr('src', e.target.result);        }                    reader.readAsDataURL(input.files[0]);    }}</script><?php /**PATH C:\xampp\htdocs\mozarc_work\application\resources\views/pages/company/form/content.blade.php ENDPATH**/ ?>